Method.Implementation:
	set ok=$$$OK
	try {
		set tempFile=pDirTemp_"\"_##class(%File).GetFilename(pFileXSLhtml)
		set ok=..TransXSL(pFileXSLhtml,tempFile)
		 
		if ##class(%File).Exists(pFileXSLexcel) {
		   set ok=##class(%File).Delete(pFileXSLexcel)
		   if 'ok quit	
		}
		Set streamIn=##class(%FileCharacterStream).%New()
	    Set streamIn.Filename=tempFile
	    Set streamIn.TranslateTable="CP1251"
	    Set streamOut=##class(%FileCharacterStream).%New()
	    Set streamOut.Filename=pFileXSLexcel
	    Set streamOut.TranslateTable="CP1251"
	    //set streamOut=streamOut.CopyFrom(..GetHeaderFile())
	    set ok=..GetHeaderFile(.streamOut)
         
        // Первый проход (Создаем table("column",nppCol)=currData)
        set startTable=0
	    set currElement="",currData="",currPar="",footer="",cond=""
	    kill table // массив для table
	    set row=0,numtable=0,widthColumn=""
 	    While 'streamIn.AtEnd {
		   Set line=streamIn.ReadLine()
		   set linetr=$zstrip(line,"<>W")
		   if linetr="" continue
		   set pp2=$zcvt($extract(linetr,1,2),"l")
		   set pp3=$zcvt($extract(linetr,1,3),"l")
		   set pp4=$zcvt($extract(linetr,1,4),"l")
		   set pp5=$zcvt($extract(linetr,1,5),"l")
		   set pp6=$zcvt($extract(linetr,1,6),"l")
		   set pp7=$zcvt($extract(linetr,1,7),"l")
		   
		   if startTable=1 {
			 if pp3="<tr" {
				 set row=row+1   
				 set currElement="tr" 
				 set currData=""
				 set cond=""
				 continue
			 }
			 if pp4="</tr" {
				 set currElement="" 
				 set currData=""
				 set cond=""
			     continue
			 }
			 if currElement="tr" {
				 if pp7="<xsl:if" {
					 set cond=linetr //w !,line b  
					 continue
				 }
				 if pp3="<td" {
					 set currElement="td"
					 set widthColumn=$tr($piece($piece(linetr,"width=",2)," ",1)," """," ")
					 set widthColumn=widthColumn*8
					 continue
				 }
			 }
			 if currElement="td" {
			    if pp4="</td" {
					 if row=1 {
						 set nppCol=$order(table("column",numtable,""),-1)+1
						 set table("column",numtable,nppCol)=$select(+widthColumn'=0:widthColumn,1:300)
						 //set table("column",numtable,nppCol)=$piece($piece(currData,"<b >",2),"</b >",1)_$c(0)_widthColumn
						 if cond'="" set table("colCondition",numtable,nppCol)=cond
			         }
			         set currElement="tr"
			         set currData=""
			         set widthColumn=""
			         set cond=""
			         continue
			     }
			     set pplast=$extract(currData,$l(currData))
			 	 set currData=currData_$select((pplast'=">")&&(pplast'=" ")&&($e(linetr,1)'="<"):" ",1:"")_linetr 
			     continue
			 }
			 
			 if pp7="</table" {
				 set currElement=""
			     set currData=""
			     set widthColumn=""
			     set row=0
			     set startTable=0
			     set cond=""
			 } 
			 continue
		   }
		   
		   if (currElement="p") {
		      if (pp3="</p") {
	                 if currPar'="" {
		                   set footer=$zstrip(currPar,"<>W") 
		                   if footer["<b >" set footer=$piece(footer,"<b >",2)
		                   if footer["</b >" set footer=$piece(footer,"</b >",1)
		                   set currPar="" 
	                 }
		             set currElement=""
		      } else {
			     set currPar=currPar_" "_linetr_" " 
		      }
	          continue
           }
		   
		   if pp6="<table" {
			   set startTable=1,row=0,currData=""
			   set numtable=numtable+1   
		   }
		   if pp2="<p" {
			   set currPar="" set currElement="p"   
		   }
		   if pp3="</p" {
			   set currPar="" set currElement=""
		   }

           continue		   
 	    } 
         
        // второй проход =============================================================
        do streamIn.Rewind()
        kill ^||output
              	    
        set startPass=0,startStyle=0,startTable=0,titleWorkSheet=""	    
	    set currElement="",currPar="",currData=""
	    set startBold=0
	    set row=0
	    set numtable=0
 	    While 'streamIn.AtEnd {
		   Set line=streamIn.ReadLine()
		   set linetr=$zstrip(line,"<>W")
		   if linetr="" continue
		   set pp1=$zcvt($extract(linetr,1),"l")
		   set pp2=$zcvt($extract(linetr,1,2),"l")
		   set pp3=$zcvt($extract(linetr,1,3),"l")
		   set pp4=$zcvt($extract(linetr,1,4),"l")
		   set pp5=$zcvt($extract(linetr,1,5),"l")
		   set pp6=$zcvt($extract(linetr,1,6),"l")
		   set pp8=$zcvt($extract(linetr,1,8),"l")
		   set pp7=$zcvt($extract(linetr,1,7),"l")
		   set pp10=$zcvt($extract(linetr,1,10),"l")
		   set pp11=$zcvt($extract(linetr,1,11),"l")
		   set pp14=$zcvt($extract(linetr,1,14),"l")
		   set pp15=$zcvt($extract(linetr,1,15),"l")
		   set pp27=$zcvt($extract(linetr,1,27),"l")
		   set pp28=$zcvt($extract(linetr,1,28),"l")
		   set lineNew=""
		   //w !,line r aaa
           //if linetr["</style" b                         
		   if startPass=1 {
			  if pp14="</xsl:variable" {
				  set startPass=0
			  }
			  continue 
		   }
		   if startStyle=1 {
			  if pp7="</style" {
				  set startStyle=0
			  }
			  continue 
		   }
		   if startTable=1 {
			  if (pp1'="<")&&(row=0)&&(line[">") continue  // Продолжение тега <table> 
		   }
           if (currElement="title")&&(pp7'="</title") {
	           set titleWorkSheet=line  
	           continue 
           }         
		   if pp5="<?xml" continue
		   if pp15="<xsl:stylesheet" continue
		   if pp11="<xsl:output" continue
		   if pp5="<html" continue
		   if pp6="</html" continue
		   if pp5="<head" continue
		   if pp6="</head" continue
		   if pp5="<meta" continue
		   if pp6="</meta" continue
		   if pp4="<div" continue
		   if pp5="</div" continue
		   if pp6="<thead" continue
		   if pp7="</thead" continue
		   if pp28="<xsl:value-of select=""$pass""" continue
		   
		   if pp27="<xsl:variable name=""pass"" >" set startPass=1 continue
		   if pp6="<style" set startStyle=1 continue
		   
		   if pp6="<title" {
			   set currElement="title"
			   continue
		   }
		   if pp7="</title" {
			   set currElement=""
			   continue
		   }
		   
     	   if pp5="<body" {
	     	   set titleWorkSheet=..TransTitleWorksheet(titleWorkSheet)
			   set lineNew="<Worksheet ss:Name="""_$select(titleWorkSheet'="":titleWorkSheet,1:"Данные 1")_""" >"
			   //do streamOut.WriteLine(lineNew)
			   set ^||output($i(^||output))=lineNew
			   do ..WritePageSetup()
			   if '$data(table) b 
			   set numtable=numtable+1 
			   do ..WriteStartTable(.streamOut,.table,.columnsCount,numtable)
			   set numtable=numtable-1
			   continue
		   }
		   if pp6="</body" {
			   //do streamOut.WriteLine("</Table>")
			   //do streamOut.WriteLine("</Worksheet>")
			   set ^||output($i(^||output))="</Table>"
			   set ^||output($i(^||output))="</Worksheet>"
			   continue
		   }
		   
		   /// Строку <!-- NextWorkSheet --> надо проставить в описании xsl для html руками
		   if linetr["NextWorkSheet" {
			   if ($data(table("column",numtable+1))) {
				   if linetr["title" {
					   set titleWorkSheet=$extract($piece($piece(linetr,"starttitle",2),"endtitle",1),1,30)   
				   }
				   if titleWorkSheet="" set titleWorkSheet="Данные "_(numtable+1)
				   set titleWorkSheet=..TransTitleWorksheet(titleWorkSheet)
				   if $get(footer)'="" {
					    set ^||output($i(^||output))="  <Row><Cell><Data ss:Type=""String"" ></Data></Cell></Row>"  //пустая чтрока
					   	set ^||output($i(^||output))="  <Row ss:StyleID=""Default"" >"
			            set ^||output($i(^||output))="    <Cell ss:MergeAcross="""_(columnsCount-1)_""" >"
			            set ^||output($i(^||output))="      <Data ss:Type=""String"" >"
			            set ^||output($i(^||output))="         "_footer
                        set ^||output($i(^||output))="      </Data>"
			            set ^||output($i(^||output))="    </Cell>"
			            set ^||output($i(^||output))="  </Row>"
				   }
				   set ^||output($i(^||output))="</Table>"
				   set ^||output($i(^||output))="</Worksheet>"
				   set ^||output($i(^||output))=line
				   set lineNew="<Worksheet ss:Name="""_$select(titleWorkSheet'="":titleWorkSheet,1:"NameWorkSheet")_""" >"
				   set ^||output($i(^||output))=lineNew   
				   do ..WritePageSetup()
				   do ..WriteStartTable(.streamOut,.table,.columnsCount,numtable+1)
		           continue
	           }
		   }
		   
		   if pp6="<table" {
			   set startTable=1,row=0,style=""
			   set numtable=numtable+1
			   //if numtable>1 do ..WriteStartTable(.streamOut,.table,.columnsCount,numtable)
			   continue
		   }
		   if pp7="</table" {
	           continue
           }

     	   if pp2="<p" {
			   set currElement="p"
			   set currPar=""
			   set ^||output($i(^||output))="  <Row ss:StyleID=""DefaultTitle"" >"
			   set ^||output($i(^||output))="    <Cell ss:MergeAcross="""_(columnsCount-1)_""" >"
			   set ^||output($i(^||output))="      <Data ss:Type=""String"" >"
			   continue
		   }
		   
           if pp3="<tr" {
	           set row=row+1
	           if row=1 {   // перед шапкой таблицы пустая строка
		          set ^||output($i(^||output))="  <Row>"_"<Cell ss:MergeAcross="""_(columnsCount-1)_""" >"_"<Data ss:Type=""String"" ></Data></Cell></Row>" 
	           }
	           set align=$piece($piece(linetr,"align",2)," ",1)
	           set align=$tr(align," =""","")
	           if align'="" set align=$zcvt($e(align,1),"U")_$zcvt($e(align,2,$length(align)),"L")
	           set style=""
	           if (align'="")||(startBold=1) {
		           set style="Default"_$select(align'="":align,1:"")_$select(startBold=1:"Bold",1:"")
	               if style="Default" set style="DefaultLeft"
	           }
	           if row=1 set style="DefaultHeaderTable"
	           if linetr["background" {
		           set style=$select(row=1:"DefaultTableLineBackgroundCenterBold",1:"DefaultTableLineBackground")
	           }
	           if row=1 {
		          set ^||output($i(^||output))="  <Row><Cell><Data ss:Type=""String"" ></Data></Cell></Row>"  //пустая чтрока   
	           }
	           set str="  <Row"
	           if style'="" set str=str_" ss:StyleID="""_style_""""
	           set colspan=$tr($piece($piece(linetr,"colspan",2)," ",1)," =""","")
	           set rowspan=$tr($piece($piece(linetr,"rowspan",2)," ",1)," =""","")
	           set str=str_$select(colspan'="":" ss:MergeAcross="""_(colspan-1)_"""",1:"")
	           set str=str_$select(rowspan'="":" ss:MergeDown="""_(rowspan-1)_"""",1:"")
	           set str=str_$select($zstrip(str,"<>W")="<Row":"",1:" ")_">"
			   set ^||output($i(^||output))=str
			   set currElement="tr",currData=""   
			   continue
		   }
		   
		   	if pp4="</tr" {
			   set ^||output($i(^||output))="  </Row>"
			   set currElement=""
 			   continue
		   }
		   
		   if pp3="<td" {
			   set npppr="",find="",stylerow="",npprow=""
			   for ii=1:1:1000 {
				   set npppr=$order(^||output(npppr),-1) if npppr="" quit
				   set reci=^||output(npppr)
			       set rec=$zstrip(reci,"<>W")
		           if ($extract(rec,1,4)="<Row") {
			           set find="row",npprow=npppr
			           set stylerow=$tr($piece($piece(rec,"ss:StyleID=",2)," ",1),"""","")   
			           quit
		           }
		           if ($extract(rec,1,5)="<Cell") {
			           set find="col"
			           set stylerow=$tr($piece($piece(rec,"ss:StyleID=",2)," ",1),"""","")   
			           quit
		           }
			   }
			   //if (stylerow="")||(stylerow="Default") set stylerow="DefaultLeft"
			   
			   set align=$piece($piece(linetr,"align",2)," ",1)
	           set align=$tr(align," =""","")
	           if align'="" set align=$zcvt($e(align,1),"U")_$zcvt($e(align,2,$length(align)),"L")
	           set style="DefaultLeft"
	           if (align'="")||(startBold=1) {
		           set style="Default"_$select(align'="":align,1:"")_$select(startBold=1:"Bold",1:"")
	               if style="Default" set style="DefaultLeft"
	           }
	           if (row=1)&&(stylerow'="") set style=stylerow
	           
	           if (stylerow'="")&&(stylerow'="DefaultTitle") {
		           if (stylerow["Left")&&(align'="")&&(align'="Left")&&(stylerow'="DefaultTableLineBackground") set stylerow=$piece(stylerow,"Left",1)_align_$piece(stylerow,"Left",2)
		           if (stylerow["Right")&&(align'="")&&(align'="Right") set stylerow=$piece(stylerow,"Right",1)_align_$piece(stylerow,"Right",2)
		           if (stylerow["Center")&&(align'="")&&(align'="Center") set stylerow=$piece(stylerow,"Center",1)_align_$piece(stylerow,"Center",2)
		           if (stylerow="DefaultTableLineBackground")&&(align'="")&&(align'="Left") set stylerow=stylerow_align
		           if (stylerow="DefaultTableLineBackgroundBold")&&(align'="")&&(align'="Left") set stylerow=$piece(stylerow,"Bold",1)_align_"Bold"
		           if (style["Bold")&&(stylerow'["Bold") set stylerow=stylerow_"Bold"
		           set style=stylerow
		           if find="row" {  // надо удалить style in Row
			           set rec=^||output(npprow),recnew=""
		               for i=1:1:$length(rec," ") {
			              set pp=$piece(rec," ",i)
			              if $extract(pp,1,11)="ss:StyleID=" continue
			              set recnew=recnew_$select(recnew'="":" ",1:"")_$select(pp="":" ",1:pp)
		               }
		               set ^||output(npprow)=recnew   
		           }
	           }
	           set colspan=$tr($piece($piece(linetr,"colspan",2)," ",1)," =""","")
	           set rowspan=$tr($piece($piece(linetr,"rowspan",2)," ",1)," =""","")
	           set str="  <Cell"_$select(style'="":" ss:StyleID="""_style_"""",1:"")
	           set str=str_$select(colspan'="":" ss:MergeAcross="""_(colspan-1)_"""",1:"")
	           set str=str_$select(rowspan'="":" ss:MergeDown="""_(rowspan-1)_"""",1:"")
	           set str=str_$select($zstrip(str,"<>W")="<Cell":"",1:" ")_">"
               set ^||output($i(^||output))=str
               set ^||output($i(^||output))="      <Data ss:Type=""String"" >"	
			   set currElement="td"
			   continue
		   }

           if pp2="<b" {
	           set startBold=1
	           set npppr=$order(^||output(""),-1)
	           if npppr'="" {
		           set reci=^||output(npppr)
		           set rec=$zstrip(reci,"<>W")
		           if ($extract(rec,1,4)="<Row") {
			          set style=$tr($piece($piece(rec,"ss:StyleID=",2)," ",1),"""","") 
			          if (style'="")&&(style'["Bold")&&(style'="DefaultHeaderTable")&&(style'="DefaultTitle") {
				          set stylenew=style_"Bold"  
				          if rec["ss:StyleID=" {
					          set ^||output(npppr)=$piece(reci,style,1)_stylenew_$piece(reci,style,2)
				          } else {
					          set ^||output(npppr)=$extract(reci,1,$length(reci)-1)_" ss:StyleID="""_stylenew_""" >" 
				          }
			          } 
		           }
		           if ($extract(rec,1,5)="<Data") {
			           set style="",firstcell=0,firstrow=0,nppcell=""
			           for ii=1:1:500 {
				           set npppr=$order(^||output(npppr),-1) if npppr="" quit
				           set reci=^||output(npppr)
			               set rec=$zstrip(reci,"<>W")
		                   if ($extract(rec,1,5)="<Cell")&&(firstcell=0) { 
		                      set firstcell=1,nppcell=npppr
			                  set style=$tr($piece($piece(rec,"ss:StyleID=",2)," ",1),"""","")    
		                   }
		                   if ($extract(rec,1,4)="<Row")&&(firstrow=0) {
			                   set firstrow=1
			                   set style=$tr($piece($piece(rec,"ss:StyleID=",2)," ",1),"""","")   
			                   quit
		                   }
			           }
			           if (nppcell'="")&&(style'="")&&(style'["Bold")&&(style'="DefaultHeaderTable")&&(style'="DefaultTitle") {
				               set stylenew=style_"Bold"    
				               set reci=^||output(nppcell)
				               set rec=$zstrip(reci,"<>W") 
				               if rec["ss:StyleID=" {
					               set style=$tr($piece($piece(rec,"ss:StyleID=",2)," ",1),"""","")
					               set ^||output(nppcell)=$piece(reci,style,1)_stylenew_$piece(reci,style,2)
				               } else {
					               set ^||output(nppcell)=$extract(reci,1,$length(reci)-1)_" ss:StyleID="""_stylenew_""" >"
				               }
			           } 
		           }
	           }
	           continue
           }
           if pp3="</b" {
	           set startBold=0
	           continue
           }
		   
           if (currElement="td") {
			   if pp4="</td" {
				   set flagConcat1=0,flagConcat2=0
				   set npppr=$order(^||output(""),-1)
	               if (npppr'="") {
		               set reci=^||output(npppr)
		               set rec=$zstrip(reci,"<>W")
		               if $extract(currData,1)'="<" {   // Пытаемся записать содержимое ячейки на одной строке с <Data , иначе много пробелов подразумевается
		                  if $extract(rec,1,5)="<Data" {
			                 set ^||output(npppr)=reci_currData
			                 set flagConcat1=1
		                  }			               
		               }
		               if $extract(currData,$length(currData))'=">" {   // Пытаемся записать </Data> на одной строке с содержимым ячейки , иначе много пробелов подразумевается
		                  set ^||output(npppr)=^||output(npppr)_"</Data>"
		                  set flagConcat2=1
		               }
	               }
				   if flagConcat1=0 {
			           set ^||output($i(^||output))="       "_currData
		           }
		           if flagConcat2=0 {
			           set ^||output($i(^||output))="      </Data>"
		           }
		           set ^||output($i(^||output))="    </Cell>"
			       set currElement=""
			       set currData="" 
			   } else {
				   set pplast=$extract(currData,$l(currData))
			 	   if (linetr'="<b >")&&(linetr'="</b >") {
				 	   //set currData=currData_$select((pplast'=">")&&(pplast'=" ")&&($extract(linetr,1)'="<"):" ",1:"")_linetr
				 	   set currData=currData_" "_linetr
			 	   }
			   }
			   continue
		   }
           
           if pp4="</td" {
	           b  // Не должно сюда приходить
		       set ^||output($i(^||output))="      </Data>"
		       set ^||output($i(^||output))="    </Cell>"
			   set currElement=""
			   set currData=""
 			   continue
		   }

           if (currElement="p") {
			   if pp3="</p" {
				   set flagConcat1=0,flagConcat2=0
				   set npppr=$order(^||output(""),-1)
	               if (npppr'="") {
		               set reci=^||output(npppr)
		               set rec=$zstrip(reci,"<>W")
		               if $extract(currData,1)'="<" {   // Пытаемся записать содержимое ячейки на одной строке с <Data , иначе много пробелов подразумевается
		                  if $extract(rec,1,5)="<Data" {
			                 set ^||output(npppr)=reci_currPar
			                 set flagConcat1=1
		                  }			               
		               }
		               if $extract(currPar,$length(currPar))'=">" {   // Пытаемся записать </Data> на одной строке с содержимым ячейки , иначе много пробелов подразумевается
		                  set ^||output(npppr)=^||output(npppr)_"</Data>"
		                  set flagConcat2=1
		               }
	               }
				   if flagConcat1=0 {
			           set ^||output($i(^||output))="       "_currPar
		           }
		           if flagConcat2=0 {
			           set ^||output($i(^||output))="      </Data>"
		           }
		           set ^||output($i(^||output))="    </Cell>"
		           set ^||output($i(^||output))="  </Row>"
			       set currElement=""
			       set currPar=""   
			   } else {
				   set pplast=$extract(currPar,$l(currPar))
			 	   if (linetr'="<b >")&&(linetr'="</b >") {
				 	   //set currPar=currPar_$select((pplast'=">")&&(pplast'=" ")&&($extract(linetr,1)'="<"):" ",1:"")_linetr
				 	   set currPar=currPar_" "_linetr 
			 	   }
			   }
			   continue
		   } 
            
   		   if pp3="</p" {
	   		   b  // Не должно сюда приходить
			   set currElement=""
			   set currPar=""
 			   continue
		   }
 
		   // write in new file
		   //do streamOut.WriteLine(line)
		   set ^||output($i(^||output))=line
		   
 	    }
 	    set npp=""
 	    for {
	 	    set npp=$order(^||output(npp),1,rec) if npp="" quit
	 	    do streamOut.WriteLine(rec)
 	    }
 	    
 	    Set ok=streamOut.SaveStream()
 	    if 'ok d $system.OBJ.DisplayError(ok)
	    Set streamIn="",streamOut=""

 } catch(e) { 
	    set ok=e.AsStatus() 
	    Set streamIn="",streamOut=""
 }
    if 'ok do $system.OBJ.DisplayError(ok)
	quit ok

do pStreamFileOut.WriteLine("<Table x:FullColumns=""1"" x:FullRows=""1"">")
for i=1:1:10 {
	do pStreamFileOut.WriteLine("<Column ss:StyleID=""Default"" ss:AutoFitWidth=""1"" ss:Width=""300""/>")
}

	set ok=$$$OK
	try {
		if $get(pFlagOneTable)'=1 set pFlagOneTable=0
		set tempFile=##class(%File).GetDirectory(pFileXSLexcel)_##class(%File).GetFilename(pFileXSLhtml)
		set ok=..TransXSL(pFileXSLhtml,tempFile)
		 
		if ##class(%File).Exists(pFileXSLexcel) {
		   set ok=##class(%File).Delete(pFileXSLexcel)
		   if 'ok quit	
		}
		Set streamIn=##class(%FileCharacterStream).%New()
	    Set streamIn.Filename=tempFile
	    Set streamIn.TranslateTable="CP1251"
	    Set streamOut=##class(%FileCharacterStream).%New()
	    Set streamOut.Filename=pFileXSLexcel
	    Set streamOut.TranslateTable="CP1251"
