				 if pFlagOneTable=1 set flagAboutTable="after"
				 continue
			 } 
			 if pp11="<xsl:for-ea" {
			     set table("ForEach")=linetr  
			     continue
			 } 
			 if pp11="</xsl:for-e" {
			     continue
			 }  
			 continue
		   }

           if (currElement="p")&&(pp3="</p") {
	           if currPar'="" {
		           if flagAboutTable="before" set tableHeader($i(tableHeader))=$zstrip(currPar,"<>W") 
