			      //if $e(linetr,$l(linetr)-2,$l(linetr))="-->" b  
			      set lineOut=linespace_$select(bounds(i-1)="<":"<",1:"")_pp_$select((bounds(i)=">"):">",1:"")
			      //w !,lineOut r aaa
			      do streamOut.WriteLine(..TransLine1(lineOut))
			   }
		   }
		}
 	    
 	    Set ok=streamOut.SaveStream()
	    Set streamIn="",streamOut=""
	    
	} catch(e) { 
	   set ok=e.AsStatus() 
	   Set streamIn="",streamOut=""
